function closeAllExpanded(){let e=document.querySelectorAll('[data-expand="expanded"]');e.forEach(function(e){e.style.height=0,e.style.opacity=0,setTimeout(function(){e.dataset.expand="hidden"},400)})}const body=document.querySelector("body");body.addEventListener("click",e=>{if(e.target&&"BUTTON"===e.target.tagName&&"button"===e.target.dataset.type){closeAllExpanded();let t=e.target.dataset.id,a=document.getElementById(t);a.style.removeProperty("height"),a.style.removeProperty("opacity"),a.dataset.expand="expanded"}if(e.target&&"BUTTON"===e.target.tagName&&"close"===e.target.dataset.action){let t=e.target.parentElement.parentElement;t.style.height=0,t.style.opacity=0,setTimeout(function(){t.dataset.expand="hidden"},400)}});