var accordion=document.querySelector(".accordion_faq");accordion.addEventListener("click",function(t){if(t.target&&t.target.classList.contains("accordion_faq-item-title")){var e=t.target.nextElementSibling;"block"===e.style.display?(e.classList.remove("show"),e.style.display="none"):(e.classList.add("show"),e.style.display="block")}});