var section=document.querySelectorAll(".spy-scroll > section"),sections={},i=0;window.onscroll=function(){Array.prototype.forEach.call(section,function(t){sections[t.id]=t.offsetTop-200});var t=document.documentElement.scrollTop||document.body.scrollTop;for(i in sections)if(sections[i]<=t){try{document.querySelector(".active").setAttribute("class"," ")}catch(t){}document.querySelector("a[href*="+i+"]").setAttribute("class","active")}};