var section=document.querySelectorAll(".spy-scroll > section"),sections={},i=0;Array.prototype.forEach.call(section,function(o){sections[o.id]=o.offsetTop-200}),window.onscroll=function(){var o=document.documentElement.scrollTop||document.body.scrollTop;for(i in sections)if(sections[i]<=o){try{document.querySelector(".active").setAttribute("class"," ")}catch(o){console.error(o)}document.querySelector("a[href*="+i+"]").setAttribute("class","active")}};